import React, { useState, useEffect } from "react";
import ModelViewer from "./ModelViewer";

function App() {
  const [selectedModel, setSelectedModel] = useState(null);

  useEffect(() => {
    console.log("ModelViewer 컴포넌트 렌더링 중");
  }, [model, modelScale]);

  const handleModelSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedModel(URL.createObjectURL(file));
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Interactive 3D Model Viewer</h1>
        <input type="file" accept=".glb" onChange={handleModelSelect} />
        {selectedModel && <ModelViewer model={selectedModel} />}
      </header>
    </div>
  );
}

export default App;
