// client/src/ModelViewer.js
import React, { useEffect, useRef } from "react";
import * as THREE from "three";
import { GLTFLoader } from "three/addons/loaders/GLTFLoader.js";

function ModelViewer({ model }) {
  const containerRef = useRef();
  let scene, camera, renderer, modelObject;

  useEffect(() => {
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth / window.innerHeight,
      0.1,
      1000
    );
    camera.position.z = 5;
    renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    containerRef.current.appendChild(renderer.domElement);

    const loader = new GLTFLoader();
    loader.load(model, (gltf) => {
      modelObject = gltf.scene;
      scene.add(modelObject);

      // 이미지 파일 로딩이 완료될 때까지 기다릴 수 있도록 수정
      const img = new Image();
      img.src = "/path/to/your/image.jpg"; // 이미지 파일 경로
      img.onload = () => {
        // 이미지 파일 로딩 완료 시 모델 렌더링
        renderer.render(scene, camera);
      };
    });

    const animate = () => {
      requestAnimationFrame(animate);
      if (modelObject) {
        modelObject.rotation.x += 0.005;
        modelObject.rotation.y += 0.005;
      }
      // 이미지 파일 로딩 전까지는 모델만 렌더링하도록 수정
      if (modelObject && img.complete) {
        renderer.render(scene, camera);
      }
    };

    animate();
  }, [model]);

  return <div ref={containerRef}></div>;
}

export default ModelViewer;
