import React, { useRef, useEffect, useState } from "react";
import * as THREE from "three";
import { GLTFLoader } from "three/addons/loaders/GLTFLoader.js";

function ModelViewer({ model }) {
  const containerRef = useRef();
  const [modelScale, setModelScale] = useState(1);
  const [loadedModel, setLoadedModel] = useState(null);
  const [backgroundColor, setBackgroundColor] = useState("white");
  const [lightType, setLightType] = useState("ambient"); // 기본값: ambient light

  const handleColorChange = (color) => {
    setBackgroundColor(color);
  };

  const rendererRef = useRef();

  useEffect(() => {
    const container = containerRef.current;
    while (container.firstChild) {
      container.removeChild(container.firstChild);
    }

    const scene = new THREE.Scene();
    const aspectRatio = 1200 / 600; // Aspect ratio based on 1000x1000
    const camera = new THREE.PerspectiveCamera(75, aspectRatio, 0.1, 1000);
    camera.position.set(0, 0, 800);

    const light = new THREE.AmbientLight(0xffffff, 1);
    scene.add(light);

    const renderer = new THREE.WebGLRenderer({
      antialias: true,
    });
    const canvasWidth = 1200; // Set canvas width
    const canvasHeight = 600; // Set canvas height
    renderer.setSize(canvasWidth, canvasHeight);
    containerRef.current.appendChild(renderer.domElement);

    rendererRef.current = renderer;

    const loader = new GLTFLoader();
    loader.load(model, (gltf) => {
      const loadedModel = gltf.scene;
      loadedModel.scale.set(modelScale, modelScale, modelScale);
      scene.add(loadedModel);
      setLoadedModel(loadedModel);
    });

    const animate = () => {
      requestAnimationFrame(animate);
      if (loadedModel) {
        loadedModel.rotation.x += 0.005;
        loadedModel.rotation.y += 0.005;
      }

      const currentRenderer = rendererRef.current;
      currentRenderer.setClearColor(new THREE.Color(backgroundColor));
      currentRenderer.render(scene, camera);
    };

    animate();
  }, [model, modelScale, backgroundColor]);

  return (
    <div>
      <input
        type="color"
        value={backgroundColor}
        onChange={(e) => handleColorChange(e.target.value)}
      />
      <div ref={containerRef}></div>
    </div>
  );
}

export default ModelViewer;
