import React, { useEffect, useState } from "react";
import "./App.css";

const App = () => {
  const [data, setData] = useState([]);
  const [anomalies, setAnomalies] = useState([]);

  // 이상 감지 알고리즘 (예: Z-점수)을 구현
  const detectAnomalies = (newData) => {
    // 이곳에서 이상을 감지하고 이상 데이터의 인덱스를 반환
    return [];
  };

  useEffect(() => {
    // 실시간 데이터 생성 및 업데이트 (예: 5초마다)
    const interval = setInterval(() => {
      // 여기에서 실시간 데이터를 생성 (예: API 호출 또는 데이터 생성 함수 사용)
      const newPoint = {
        timestamp: new Date().toISOString(),
        temperature: Math.random() * 30 + 10, // 예시: 10에서 40 사이의 온도
        pressure: Math.random() * 30 + 900, // 예시: 900에서 930 사이의 압력
        level1_chemical: Math.random() * 50 + 50, // 예시: 50에서 100 사이의 화학 농도
        level2_chemical: Math.random() * 50 + 50, // 예시: 50에서 100 사이의 화학 농도
      };

      // 새 데이터 업데이트
      setData((prevData) => [newPoint, ...prevData]);

      // 이상 감지
      const detectedAnomalies = detectAnomalies([newPoint, ...prevData]);
      setAnomalies(detectedAnomalies);
    }, 5000); // 5초마다 데이터 업데이트

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>화학 공장 모니터링</h1>
      </header>
      <div className="data-container">
        <h2>실시간 데이터</h2>
        <table>
          <thead>
            <tr>
              <th>시간</th>
              <th>온도</th>
              <th>압력</th>
              <th>화학1</th>
              <th>화학2</th>
            </tr>
          </thead>
          <tbody>
            {data.map((point, index) => (
              <tr
                key={index}
                className={anomalies.includes(index) ? "anomaly" : ""}
              >
                <td>{point.timestamp}</td>
                <td>{point.temperature.toFixed(2)}</td>
                <td>{point.pressure.toFixed(2)}</td>
                <td>{point.level1_chemical.toFixed(2)}</td>
                <td>{point.level2_chemical.toFixed(2)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default App;
