// client/src/ModelViewer.js
import React, { useRef, useEffect, useState } from "react";
import * as THREE from "three";
import { GLTFLoader } from "three/addons/loaders/GLTFLoader.js";

function ModelViewer({ model }) {
  const containerRef = useRef();
  const [modelScale, setModelScale] = useState(1);
  const [loadedModel, setLoadedModel] = useState(null); // loadedModel 상태 추가

  useEffect(() => {
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth / window.innerHeight,
      0.1,
      1000
    );
    camera.position.set(0, 0, 5);

    // scene.background = new THREE.Color("black");

    const renderer = new THREE.WebGLRenderer({
      antialias: true,
    });
    renderer.setSize(window.innerWidth, window.innerHeight);
    containerRef.current.appendChild(renderer.domElement);

    const loader = new GLTFLoader();
    loader.load(model, (gltf) => {
      const loadedModel = gltf.scene;
      loadedModel.scale.set(0, 80, 80);
      scene.add(loadedModel);
      setLoadedModel(loadedModel); // loadedModel 상태 업데이트
    });

    const animate = () => {
      requestAnimationFrame(animate);
      if (loadedModel) {
        loadedModel.scale.set(modelScale, modelScale, modelScale);
      }
      renderer.render(scene, camera);
    };

    animate();
  }, [model, modelScale]);

  return (
    <div ref={containerRef}>
      <input
        type="range"
        min="0.1"
        max="2"
        step="0.1"
        value={modelScale}
        onChange={(e) => setModelScale(parseFloat(e.target.value))}
      />
    </div>
  );
}

export default ModelViewer;
