// client/src/ModelViewer.js
import React, { useRef, useEffect, useState } from "react";
import * as THREE from "three";
import { GLTFLoader } from "three/addons/loaders/GLTFLoader.js";

const scene = new THREE.Scene();
const camera = new THREE.PerspectiveCamera(
  75,
  window.innerWidth / window.innerHeight,
  0.1,
  1000
);
const renderer = new THREE.WebGLRenderer({ antialias: true });

function ModelViewer({ model }) {
  const containerRef = useRef();
  const [modelScale, setModelScale] = useState(1);
  const [loadedModel, setLoadedModel] = useState(null);

  useEffect(() => {
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth / window.innerHeight,
      0.1,
      1000
    );
    camera.position.set(0, 0, 800);

    // 조명 추가
    // const light = new THREE.PointLight(0xffffff, 1);
    // light.position.set(1, 1, 1);
    // scene.add(light);

    //AmbientLight : 자연광
    const color = 0xffffff;
    const intensity = 1;
    const light = new THREE.AmbientLight(color, intensity);
    scene.add(light);

    //DirectionalLight : 직사광, 태양표현
    // const color = 0xffffff;
    // const intensity = 1;
    // const light = new THREE.DirectionalLight(color, intensity);
    // //light의 위치와 target의 위치를 지정한다
    // light.position.set(0, 10, 0);
    // light.target.position.set(-5, 0, 0);
    // scene.add(light);
    // scene.add(light.target);

    //HemisphereLight : 반구광
    //천장색(skycolor)과 바닥색(groundcolor) 혼합
    // const skyColor = 0xb1e1ff; // 하늘색
    // const groundColor = 0xb97a20; // 오렌지 브라운
    // const intensity = 1;
    // const light = new THREE.HemisphereLight(skyColor, groundColor, intensity);
    // scene.add(light);

    // scene.background 설정 (원하는 배경색으로 설정)
    scene.background = new THREE.Color("white");

    const renderer = new THREE.WebGLRenderer({
      antialias: true,
    });
    renderer.setSize(window.innerWidth, window.innerHeight);
    containerRef.current.appendChild(renderer.domElement);

    const loader = new GLTFLoader();
    loader.load(model, (gltf) => {
      const newModel = gltf.scene;
      newModel.scale.set(modelScale, modelScale, modelScale);

      if (loadedModel) {
        scene.remove(loadedModel);
      }

      scene.add(newModel);
      setLoadedModel(newModel);
    });

    const animate = () => {
      requestAnimationFrame(animate);
      if (loadedModel) {
        loadedModel.scale.set(modelScale, modelScale, modelScale);
      }
      renderer.render(scene, camera);
    };

    animate();
  }, [model, modelScale]);

  return (
    <div ref={containerRef}>
      <input
        type="range"
        min="0.1"
        max="2"
        step="0.1"
        value={modelScale}
        onChange={(e) => setModelScale(parseFloat(e.target.value))}
      />
    </div>
  );
}

export default ModelViewer;
