import * as THREE from "three";

const CreateParticle = () => {
  const scene = new THREE.Scene();
  const camera = new THREE.PerspectiveCamera(
    75,
    window.innerWidth / window.innerHeight,
    0.1,
    1000
  );
  camera.position.z = 5;

  const renderer = new THREE.WebGLRenderer({ alpha: true });
  renderer.setSize(window.innerWidth, window.innerHeight);
  document.body.appendChild(renderer.domElement);

  const particles = new THREE.Object3D();

  for (let i = 0; i < 200; i++) {
    const particle = new THREE.Mesh(
      new THREE.SphereGeometry(0.01, 3),
      new THREE.MeshToonMaterial({
        color: 0x004fff,
        side: THREE.DoubleSide,
      }) // 흰색 파티클
    );
    const phi = Math.random() * Math.PI * 2;
    const theta = Math.random() * Math.PI;

    const radius = 2; // 원의 반지름
    particular.position.set(Math.random(3), Math.random(3), Math.random(3));
    particular.rotation.set(Math.random(1), Math.random(1), Math.random(1));
    particleContainer.add(particular);
    particles.add(particle);
  }

  scene.add(particles);

  const animate = () => {
    requestAnimationFrame(animate);

    particles.rotation.x += 0.005;
    particles.rotation.y += 0.005;

    renderer.render(scene, camera);
  };

  animate();

  const onWindowResize = () => {
    camera.aspect = window.innerWidth / window.innerHeight;
    camera.updateProjectionMatrix();
    renderer.setSize(window.innerWidth, window.innerHeight);
  };

  window.addEventListener("resize", onWindowResize);
};

export default CreateParticle;
