// client/src/ModelViewer.js
import React, { useEffect, useRef, useState } from "react";
import * as THREE from "three";
import { GLTFLoader } from "three/addons/loaders/GLTFLoader.js";

function ModelViewer({ model }) {
  const containerRef = useRef();
  let scene, camera, renderer, modelObject;

  const [rotation, setRotation] = useState({ x: 0, y: 0 }); // 초기 회전값

  useEffect(() => {
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth / window.innerHeight,
      0.1,
      1000
    );
    camera.position.z = 5;
    camera.rotation.x = rotation.x;
    camera.rotation.y = rotation.y;
    renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    containerRef.current.appendChild(renderer.domElement);

    const loader = new GLTFLoader();
    loader.load(model, (gltf) => {
      modelObject = gltf.scene;
      scene.add(modelObject);
    });

    const animate = () => {
      requestAnimationFrame(animate);

      if (modelObject) {
        modelObject.rotation.x += 0.005;
        modelObject.rotation.y += 0.005;
      }

      camera.rotation.x = rotation.x;
      camera.rotation.y = rotation.y;

      renderer.render(scene, camera);
    };

    animate();
  }, [model, rotation]);

  const rotateCamera = (direction) => {
    // 상하좌우로 카메라 회전
    const newRotation = { ...rotation };
    switch (direction) {
      case "up":
        newRotation.x += 0.1;
        break;
      case "down":
        newRotation.x -= 0.1;
        break;
      case "left":
        newRotation.y -= 0.1;
        break;
      case "right":
        newRotation.y += 0.1;
        break;
      default:
        break;
    }
    setRotation(newRotation);
  };

  return (
    <div ref={containerRef}>
      <button onClick={() => rotateCamera("up")}>Rotate Up</button>
      <button onClick={() => rotateCamera("down")}>Rotate Down</button>
      <button onClick={() => rotateCamera("left")}>Rotate Left</button>
      <button onClick={() => rotateCamera("right")}>Rotate Right</button>
    </div>
  );
}

export default ModelViewer;
