// client/src/ModelViewer.js
import React, { useEffect, useRef, useState } from "react";
import * as THREE from "three";
import { GLTFLoader } from "three/addons/loaders/GLTFLoader.js";

function ModelViewer({ model }) {
  const containerRef = useRef();
  let scene, camera, renderer, modelObject;

  const [position, setPosition] = useState({ x: 0, y: 0, z: 5 }); // 초기 위치

  useEffect(() => {
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth / window.innerHeight,
      0.1,
      1000
    );
    camera.position.set(position.x, position.y, position.z);
    renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    containerRef.current.appendChild(renderer.domElement);

    const loader = new GLTFLoader();
    loader.load(model, (gltf) => {
      modelObject = gltf.scene;
      scene.add(modelObject);
    });

    const animate = () => {
      requestAnimationFrame(animate);

      if (modelObject) {
        modelObject.rotation.x += 0.005;
        modelObject.rotation.y += 0.005;
      }

      renderer.render(scene, camera);
    };

    animate();
  }, [model, position]);

  const moveModel = (direction) => {
    // 상하좌우로 모델 이동
    const newPosition = { ...position };
    switch (direction) {
      case "up":
        newPosition.y += 1;
        break;
      case "down":
        newPosition.y -= 1;
        break;
      case "left":
        newPosition.x -= 1;
        break;
      case "right":
        newPosition.x += 1;
        break;
      default:
        break;
    }
    setPosition(newPosition);
  };

  return (
    <div ref={containerRef}>
      <button onClick={() => moveModel("up")}>Move Up</button>
      <button onClick={() => moveModel("down")}>Move Down</button>
      <button onClick={() => moveModel("left")}>Move Left</button>
      <button onClick={() => moveModel("right")}>Move Right</button>
    </div>
  );
}

export default ModelViewer;
